const directTaskEdit = (String(env.get("NODERED_DIRECT_TASK_EDIT")).toLowerCase() === "true");

if (directTaskEdit && msg._client?.socketId == msg.payload?.userTask?.correlationId) {
    msg._openDialog = true;
} else {
    msg._openDialog = false;

