const key = `messages-${msg.userId}`;
const messages = global.get(key) ?? [];

const toolMessage = msg.payload[0].output;
const toolCallId = msg.payload[0].tool_call_id

messages.push({
    "role": "tool",
    "content": toolMessage,
    "tool_call_id": toolCallId
});

global.set(key, messages);

return msg;
